# BFS搜索的时候。移动的范围是连续的，所以用bisect 查找区间
# https://leetcode.cn/problems/minimum-operations-to-equalize-binary-string/
from typing import List, Tuple, Optional

from collections import defaultdict,deque

from sortedcontainers import SortedDict,SortedList
import math
INF  = math.inf


class Solution:
    def minOperations(self, s: str, k: int) -> int:
        n = len(s)
        dp = [10**10]*(n+1)
        ones = s.count("1")
        sq  = deque([(ones, 0)])
        dp[ones] = 0
        sl0 = SortedList([i for i in range(0,n+1,2)])
        sl1 = SortedList([i for i in range(1,n+1,2)])
        sl = [sl0,sl1]
        sl[ones%2].remove(ones)
        while sq: 
            ones,cost = sq.popleft()
            zeros = n-ones
            rm = []
            zs = min(zeros,k)
            os = min(ones,k)
            frv = ones -os + k-os
            tov = ones +zs -(k-zs)
            fidx = sl[(ones+k)%2].bisect_left(frv)
            tidx = sl[(ones+k)%2].bisect_right(tov)
            for idx in range(fidx,tidx):
                rm.append(sl[(ones+k)%2][idx])
            #print(rm,ones,frv,tov,zs,os)
            for a in rm :
                dp[a] = cost +1
                sl[(ones+k)%2].remove(a)
                sq.append((a,cost+1))

        return dp[-1] if dp[-1]!= 10**10 else -1
        

        


