import heapq
class Solution(object):
    def minCost(self, maxTime, edges, passingFees):
        """
        :type maxTime: int
        :type edges: List[List[int]]
        :type passingFees: List[int]
        :rtype: int
        """
        h =[]
        maxN = 0
        for edge in edges:
            if edge[1] > maxN:
                maxN = edge[1]
            if edge[0] > maxN:
                maxN = edge[0]
        n = maxN +1
        arr = [] 
        costAndTime =[]
        visited =[]
        for i in range(0,n):
            arr.append([])
            visited.append(0)
            costAndTime.append([10000000000,1000000000])
        costAndTime[0]=[passingFees[0],0]
        for edge in edges:
            i = edge[0]
            j = edge[1]
            time  = edge[2]
            arr[i].append([j,time])
            arr[j].append([i,time])
        heapq.heappush(h,(0,0,0,passingFees[0]))
        #print(arr)
        while len(h)>0:
            temp = heapq.heappop(h)
            i = temp[1]
            visited[i] =1
            if i == maxN:
                return costAndTime[i][0] 
            for line in arr[i]:
                j = line[0]
                tempTime = temp[2] + line[1]
                #print(j,tempTime)
                if visited[j] ==0 and  tempTime <= maxTime:
                    tempCost = temp[3] + passingFees[j]
                    # if tempTime < costAndTime[j][1]:
                    #     costAndTime[j][1]=tempTime
                    if tempCost < costAndTime[j][0]:
                        costAndTime[j][0] = tempCost
                    heapq.heappush(h,(tempTime,j,tempTime,tempCost))
        #print(visited)
        #print(arr)
        return -1



