from collections import Counter
class Solution(object):
    def minimumWhiteTiles(self, floor, numCarpets, carpetLen):
        """
        :type floor: str
        :type numCarpets: int
        :type carpetLen: int
        :rtype: int
        """
        n = len(floor)
        floor = floor + "0"*carpetLen
        cnt = Counter(floor)
        if numCarpets*carpetLen >= n:
            return 0
        zeros = cnt["1"]
        tms =[0]*n
        ls =[0]*(n+carpetLen+1)
        for i,a in enumerate(floor):
            if a =="1":
                ls[i+1] =ls[i] +1
            else:
                ls[i+1] = ls[i]
        #print(ls)
        for i in range(n):
            tms[i] =ls[i+carpetLen] - ls[i]
        #print(tms)
        dp= [[0]*(n) for _ in range(numCarpets)]
        for i in range(numCarpets):
            for j in range(n):
                if i == 0:
                    if j !=0:
                        dp[i][j] = max(tms[j],dp[i][j-1])
                    else:
                        dp[i][j] =tms[j]
                else:
                    if j < carpetLen:
                        dp[i][j] = dp[i-1][j]
                    else:
                        dp[i][j] = max(dp[i][j-1],  dp[i-1][j-carpetLen] + tms[j])
        return zeros - max(dp[numCarpets-1])

re = Solution().minimumWhiteTiles(floor = "10110101", numCarpets = 2, carpetLen = 2)
