from collections import Counter
class Solution(object):
    def minimumWhiteTiles(self, floor, numCarpets, carpetLen):
        """
        :type floor: str
        :type numCarpets: int
        :type carpetLen: int
        :rtype: int
        """
        n = len(floor)
        state =[a for a in floor]
        idx =0
        for i in range(numCarpets):
            idx =0
            mx = 0
            gain = 0
            for k in range(carpetLen):                
                if state[k] =="1":
                    gain +=1
            mx = gain
            for j in range(n-carpetLen):
                if state[j] == "1":
                    gain -=1
                if state[j+carpetLen]=="1":
                    gain +=1
                if gain > mx:
                    idx = j+1
                    mx = gain
            #print(mx,idx)
            for k in range(carpetLen):
                state[idx +k] ="0"
        cnt = Counter(state)
        return cnt["1"]

