class Solution:
    def beautifulPartitions(self, s: str, k: int, minLength: int) -> int:
        if k ==1 :return 1
        prime = {'2', '3', '5', '7'}
        n = len(s)
        cut =[]
        for i in range(1,n-1):
            if s[i] not in prime and s[i+1] in prime:
                cut.append(i)
        #print(cut,n)
        m = len(cut)
        dp= [[0]*m for _ in range(k)]
        for i,c in enumerate(cut):
            if c +1 >= minLength and n-c-1 >= minLength:
                #print(n-c-1,c)
                dp[0][i] =1
        mod = 10**9+7
        for i in range(1,k):
            for j in range(m):
                for z in range(j):
                    if dp[i-1][z]>0 and cut[j]-cut[z]>=minLength and n-cut[j]-1 >= minLength:
                        dp[i][j]  += dp[i-1][z]
                        dp[i][j]%=mod
        #print(dp,k)
        
        return sum(dp[k-2])%mod 



