def find_horizontal_line(events):
    
    
    # Sort events by y-coordinate
    events.sort()
    
    # Initialize active intervals and total area
    active = SortedList([])
    total_area = 0
    prev_y = None
    psm = None
    for event in events:
        y, typ, x1, x2 = event

        
        if typ == 'astart':
            active.add((x1,+1))
            active.add((x2,-1))
        else:
            active.remove((x1,+1))
            active.remove((x2,-1))
        
        acc = 0 
        sm =0
        lst = -1
        for a,t in active:
            if acc ==0:
                lst =a 
                acc += t 
            else:
                sm += a -lst
                lst = a 
                acc += t
        if psm != None:
            total_area += psm *(y-prev_y)
        #print(active,y,total_area)
        prev_y = y
        psm = sm
    
    return total_area


class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        events = []
        mxy = 0
        for x, y, l in squares:
            events.append((y, 'astart', x, x + l))
            events.append((y + l, 'end', x, x + l))
            mxy= max(mxy,y+l)
        totalS = find_horizontal_line(events)
        

        def verify(mid):
            sm = 0 
            events = []
            for x,y,l in squares:
                if y > mid: continue 
                t = min(y+l,mid)
                events.append((y, 'astart', x, x + l))
                events.append((t, 'end', x, x + l))
            return find_horizontal_line(events) *2
        l,r = 0,mxy*2

        while l + 10**(-6) <r:
            mid = (l+r)/2
            t= verify(mid)
            if t >=totalS:
                r= mid 
            elif t < totalS:
                l = mid 
            #print(l,r)
        return l



