
class Solution(object):
    def rangeSum(self, nums, n, left, right):
        """
        :type nums: List[int]
        :type n: int
        :type left: int
        :type right: int
        :rtype: int
        """
        pls = [0]*(n+1)
        for i in range(n):
            pls[i+1] = pls[i] + nums[i]
        ls = [0]*(n*(n+1)//2)
        idx =0
        for i in range(n):
            for j in range(i,n):
                ls[idx] = pls[j+1] - pls[i]
                idx +=1
        ls.sort()
        sm = sum(ls[left-1:right]) % (10**9+7)
        #print(ls[left-1:right])
        #print(ls)
        return sm


