
class Solution(object):
    def minRefuelStops(self, target, startFuel, stations):
        """
        :type target: int
        :type startFuel: int
        :type stations: List[List[int]]
        :rtype: int
        """
        stations = stations
        n = len(stations)
        dp = [startFuel]*(n+1)
        
        for i,(p,f) in enumerate(stations):
            for j in range(i,-1,-1):
                if dp[j]  >= p:
                    dp[j+1] = max(dp[j+1],dp[j] + f)
        for i,v in enumerate(dp):
            if v >= target:
                return i
        return -1

